import React, { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Box,
  Button,
  TextField,
  Typography,
  Grid,
  RadioGroup,
  FormControlLabel,
  Radio,
  Checkbox,
  FormGroup,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

function Login() {
  const [isSignUp, setisSignUp] = useState(false);
  const [gender, setGender] = useState("");
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(
    localStorage.getItem("login") === "true"
  );

  const mystyle = {
    backgroundImage: "url('backgroundimg.jpg')",
    backgroundSize: "cover",
    minHeight: "100vh",
    width: "100vw",
    padding: "50px",
    backgroundRepeat: "no-repeat",
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
    getValues,
  } = useForm({});

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const validateConfirmPassword = (value) => {
    const { password } = getValues();
    return value === password || "Passwords do not match";
  };

  const loginHandler = (data) => {
    localStorage.setItem("login", true);
    setIsLoggedIn(true);
    alert("Logged in Successfully!");
    navigate("/");
  };

  const logoutHandler = () => {
    localStorage.removeItem("login");
    setIsLoggedIn(false);
  };

  if (isLoggedIn) {
    return (
      <div style={mystyle}>
        <Box
          display={"flex"}
          borderStyle={"solid"}
          borderColor={"blue"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            You are already logged in.
          </Typography>
          <Button
            onClick={logoutHandler}
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            Logout
          </Button>
        </Box>
      </div>
    );
  }

  return (
    <div style={mystyle}>
      <form onSubmit={handleSubmit(loginHandler)}>
        <Box
          display={"flex"}
          borderStyle={"solid"}
          borderColor={"blue"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            {isSignUp ? "Sign Up" : "Login"}
          </Typography>

          {!isSignUp && (
            <>
              <TextField
                type="text"
                name="username"
                {...register("username", {
                  required: true,
                  minLength: 3,
                })}
                variant="outlined"
                placeholder="Username"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.username && errors.username.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Username is required.
                </p>
              )}
              {errors.username && errors.username.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Username should be at least 3 characters.
                </p>
              )}

              <TextField
                type="password"
                name="password"
                {...register("password", {
                  required: true,
                  minLength: 6,
                })}
                variant="outlined"
                placeholder="Password"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.password && errors.password.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password is required.
                </p>
              )}
              {errors.password && errors.password.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password should be at least 6 characters.
                </p>
              )}
            </>
          )}

          {isSignUp && (
            <>
              <TextField
                type="text"
                name="username"
                {...register("username", {
                  required: true,
                  minLength: 3,
                })}
                variant="outlined"
                placeholder="Username"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.username && errors.username.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Username is required.
                </p>
              )}
              {errors.username && errors.username.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Username should be at least 3 characters.
                </p>
              )}

              <TextField
                type="email"
                name="email"
                {...register("email", {
                  required: true,
                  pattern: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
                })}
                variant="outlined"
                placeholder="Email"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.email && errors.email.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Email is required.
                </p>
              )}
              {errors.email && errors.email.type === "pattern" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Email is not valid.
                </p>
              )}

              <TextField
                type="password"
                name="password"
                {...register("password", {
                  required: true,
                  minLength: 6,
                })}
                variant="outlined"
                placeholder="Password"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.password && errors.password.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password is required.
                </p>
              )}
              {errors.password && errors.password.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password should be at least 6 characters.
                </p>
              )}

              <TextField
                type="password"
                variant="outlined"
                name="confirmpassword"
                {...register("confirmpassword", {
                  required: true,
                  validate: validateConfirmPassword,
                })}
                placeholder="Confirm Password"
                fullWidth
                sx={{ marginBottom: 2 }}
              />
              {errors.confirmpassword &&
                errors.confirmpassword.type === "required" && (
                  <p className="errorMsg" style={{ color: "red" }}>
                    Confirm Password is required.
                  </p>
                )}
              {errors.confirmpassword && (
                <p className="errorMsg" style={{ color: "red" }}>
                  {errors.confirmpassword.message}
                </p>
              )}

              <RadioGroup
                row
                value={gender}
                onChange={handleGenderChange}
                sx={{ marginBottom: 2 }}
              >
                <FormControlLabel
                  value="female"
                  control={<Radio />}
                  label="Female"
                  {...register("gender", {
                    required: "Please select your gender",
                  })}
                />
                <FormControlLabel
                  value="male"
                  control={<Radio />}
                  label="Male"
                  {...register("gender")}
                />
                <FormControlLabel
                  value="other"
                  control={<Radio />}
                  label="Other"
                  {...register("gender")}
                />
              </RadioGroup>
              {errors.gender && (
                <p className="errorMsg" style={{ color: "red" }}>
                  {errors.gender.message}
                </p>
              )}

              <FormGroup row>
                <FormControlLabel
                  control={
                    <Checkbox
                      value="JavaScript"
                      {...register("skills", {
                        required: "Please select at least one skill",
                      })}
                    />
                  }
                  label="JavaScript"
                />
                <FormControlLabel
                  control={<Checkbox value="React" {...register("skills")} />}
                  label="React"
                />
                <FormControlLabel
                  control={<Checkbox value="Nodejs" {...register("skills")} />}
                  label="Nodejs"
                />
                <FormControlLabel
                  control={<Checkbox value="Angular" {...register("skills")} />}
                  label="Angular"
                />
              </FormGroup>
              {errors.skills && (
                <p className="errorMsg" style={{ color: "red" }}>
                  {errors.skills.message}
                </p>
              )}
            </>
          )}

          <Button
            type="submit"
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            {isSignUp ? "Sign Up" : "Login"}
          </Button>

          <Button
            onClick={() => setisSignUp(!isSignUp)}
            sx={{ marginTop: 3, borderRadius: 3 }}
          >
            Change to {isSignUp ? "Login" : "Sign Up"}
          </Button>
        </Box>
      </form>
    </div>
  );
}

export default Login;

