import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { Box, Button, TextField, Typography, Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
  const [isLoggedIn, setIsLoggedIn] = useState(
    sessionStorage.getItem("login") === "true"
  );
  const navigate = useNavigate();
  const sessionToken = sessionStorage.getItem("token");

  const mystyle = {
    backgroundImage: "url('backgroundimg.jpg')",
    backgroundSize: "cover",
    minHeight: "100vh",
    width: "100vw",
    padding: "50px",
    backgroundRepeat: "no-repeat",
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({});

  const loginHandler = async (data) => {
    // sessionStorage.setItem("login", true);

    const result = await axios.post("http://localhost:5000/login", data, {
      headers: {
        "Content-Type": "application/json",
      },
    });
    console.log("result from login", result);
    if (result.data.auth) {
      sessionStorage.setItem("login", "true");
      sessionStorage.setItem("user", JSON.stringify(result.data.user));
      sessionStorage.setItem("token", result.data.auth);
      const sessionToken = result.data.auth;
      console.log("sessionToken:", sessionToken);
      await getData(sessionToken);
      // console.log("Token stored in sessionStorage:", result.data.auth);
      alert("Logged in Successfully!!");
      setIsLoggedIn(true);
      // getData();
      navigate("/");
    } else {
      alert("Login failed, please check your credentials.");
    }
    // alert("Logged in Successfully!!");
    // setIsLoggedIn(true);
  };
  const getData = async (sessionToken) => {
    // let sessionToken = sessionStorage.getItem("token");
    let result = await axios.get("http://localhost:5000/getData", {
      headers: {
        authorization: `bearer ${sessionToken}`,
      },
    });
    console.log("result from getdata", result.data);
  };

  // useEffect(() => {
  //   if (sessionToken) {
  //     console.log("Session Token:", sessionToken);
  //     getData();
  //   }
  // }, [sessionToken]); // Dependency on sessionToken

  const handleLogout = () => {
    // sessionStorage.removeItem("login");
    // setIsLoggedIn(false);
    // alert("Logged out Successfully!!");
    sessionStorage.removeItem("login");
    sessionStorage.removeItem("user");
    sessionStorage.removeItem("token");
    setIsLoggedIn(false);
    alert("Logged out Successfully!!");
  };
  // useEffect(() => {
  //   const token = sessionStorage.getItem("token");
  //   if (token) {
  //     setIsLoggedIn(true);
  //     getData(); // Call getData only if token is available
  //   } else {
  //     setIsLoggedIn(false);
  //   }
  // });

  if (isLoggedIn) {
    return (
      <div style={mystyle}>
        <Box
          display={"flex"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            You are Already logged in!
          </Typography>
          <Button
            onClick={handleLogout}
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            Logout
          </Button>
        </Box>
      </div>
    );
  }

  return (
    <div style={mystyle}>
      <form onSubmit={handleSubmit(loginHandler)}>
        <Box
          display={"flex"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            Login
          </Typography>

          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <Typography variant="body1">Email</Typography>
            </Grid>
            <Grid item xs={8}>
              <TextField
                type="email"
                style={{ background: "transparent" }}
                name="email"
                {...register("email", {
                  required: true,
                  //minLength: 3,
                })}
                variant="outlined"
                placeholder="Email"
                fullWidth
              />
              {errors.email && errors.email.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Email is required.
                </p>
              )}
              {/* {errors.username && errors.username.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Username should be at least 3 characters.
                </p>
              )} */}
            </Grid>
          </Grid>

          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <Typography variant="body1">Password</Typography>
            </Grid>
            <Grid item xs={8}>
              <TextField
                type="password"
                name="password"
                {...register("password", {
                  required: true,
                  minLength: 6,
                })}
                variant="outlined"
                placeholder="Password"
                fullWidth
              />
              {errors.password && errors.password.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password is required.
                </p>
              )}
              {errors.password && errors.password.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password should be at least 6 characters.
                </p>
              )}
            </Grid>
          </Grid>

          <Button
            type="submit"
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            Login
          </Button>
          <Button
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
            onClick={() => navigate("/register")}
          >
            Register
          </Button>
        </Box>
      </form>
    </div>
  );
}

export default Login;
