export const addToCart = (data) => {
  return {
    type: "Add_To_Cart",
    payload: data,
  };
};
export const removeFromCart = (data) => {
  return {
    type: "Remove_From_Cart",
    payload: data,
  };
};

export const updateQuantity = (item, quantity) => ({
  type: "UPDATE_QUANTITY",
  payload: { item, quantity },
});
