import React, { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Box,
  Button,
  Select,
  TextField,
  Typography,
  InputLabel,
  MenuItem,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Grid,
  Checkbox,
  FormGroup,
} from "@mui/material";

function Authentication() {
  const [isSignUp, setisSignUp] = useState(false);
  const [State, setState] = useState("");
  const [gender, setGender] = useState("");
  const [languages, setLanguages] = useState({
    english: false,
    marathi: false,
    hindi: false,
  });
  const [dob, setDob] = useState("");
  const [contactNo, setContactNo] = useState("");
  const {
    register,
    handleSubmit,
    formState: { errors },
    trigger,
  } = useForm();
  const handleStateChange = (event) => {
    setState(event.target.value);
    trigger("state");
  };
  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };
  const handleDobChange = (event) => {
    setDob(event.target.value);
  };
  const handleLanguageChange = (event) => {
    setLanguages({
      ...languages,
      [event.target.name]: event.target.checked,
    });
  };
  const handleContactNoChange = (event) => {
    const value = event.target.value;
    if (/^\d*$/.test(value)) {
      setContactNo(value);
    }
  };
  const onSubmit = (data) => {
    console.log(data);
    alert("Logged In Successfully !!");
  };
  // console.log(isSignUp);
  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Box
          display={"flex"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={600}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            {isSignUp ? "SignUp" : "Login"}
          </Typography>
          {isSignUp && (
            <>
              <Grid
                container
                alignItems="center"
                sx={{ marginBottom: 1 }}
                spacing={1}
              >
                <Grid item xs={4}>
                  <InputLabel sx={{ marginBottom: 1 }}> UserName</InputLabel>
                </Grid>
                <Grid item xs={8}>
                  <div style={{ position: "relative" }}>
                    <TextField
                      type="text"
                      name="username"
                      {...register("username", {
                        required: true,
                        minLength: 3,
                      })}
                      variant="outlined"
                      placeholder="UserName"
                      fullWidth
                      sx={{ marginBottom: 1 }}
                    />
                    {errors.username && errors.username.type === "required" && (
                      <p
                        className="errorMsg"
                        style={{
                          color: "red",
                          position: "absolute",
                          bottom: -30,
                          left: 0,
                        }}
                      >
                        Username is required.
                      </p>
                    )}
                    {errors.username &&
                      errors.username.type === "minLength" && (
                        <p
                          className="errorMsg"
                          style={{
                            color: "red",
                            position: "absolute",
                            bottom: -30,
                            left: 0,
                          }}
                        >
                          Username should be at-least 3 characters.
                        </p>
                      )}
                  </div>
                </Grid>
              </Grid>
              <Grid
                container
                alignItems="center"
                sx={{ marginBottom: 1 }}
                spacing={2}
              >
                <Grid item xs={4}>
                  <InputLabel sx={{ marginBottom: 1 }}>State</InputLabel>
                </Grid>
                <Grid item xs={8}>
                  <div style={{ position: "relative" }}>
                    <FormControl
                      variant="outlined"
                      fullWidth
                      sx={{ margin: 0 }}
                    >
                      <Select
                        value={State}
                        name="state"
                        {...register("state", {
                          required: "State is required",
                        })}
                        onChange={handleStateChange}
                        label="State"
                        sx={{ marginBottom: 0 }}
                      >
                        <MenuItem value={1}>Maharashtra</MenuItem>
                        <MenuItem value={2}>Karnataka</MenuItem>
                        <MenuItem value={3}>Kerala</MenuItem>
                      </Select>
                      {errors.state && errors.state.type === "required" && (
                        <p
                          className="errorMsg"
                          style={{
                            color: "red",
                            position: "absolute",
                            bottom: -30,
                            left: 0,
                          }}
                        >
                          {errors.state.message}
                        </p>
                      )}
                    </FormControl>
                  </div>
                </Grid>
              </Grid>
              <Grid
                container
                alignItems="center"
                spacing={2}
                sx={{ marginBottom: 1 }}
              >
                <Grid item xs={4}>
                  <FormLabel sx={{ marginBottom: 1 }}>Gender</FormLabel>
                </Grid>
                <Grid item xs={8}>
                  <div style={{ position: "relative" }}>
                    <RadioGroup
                      row
                      value={gender}
                      controlId="gender"
                      onChange={handleGenderChange}
                      sx={{ marginBottom: 0 }}
                    >
                      <FormControlLabel
                        value="female"
                        control={<Radio />}
                        label="Female"
                        {...register("gender", {
                          required: "Please select your gender",
                        })}
                      />
                      <FormControlLabel
                        value="male"
                        control={<Radio />}
                        label="Male"
                        {...register("gender")}
                      />
                      <FormControlLabel
                        value="other"
                        control={<Radio />}
                        label="Other"
                        {...register("gender")}
                      />
                      {errors.gender && (
                        <p className="errorMsg" style={{ color: "red" }}>
                          {errors.gender.message}
                        </p>
                      )}
                    </RadioGroup>
                  </div>
                </Grid>
              </Grid>
              <Grid
                container
                alignItems="center"
                spacing={2}
                sx={{ marginBottom: 2 }}
              >
                <Grid item xs={4}>
                  <InputLabel sx={{ marginBottom: 0 }}>
                    Date of Birth
                  </InputLabel>
                </Grid>
                <Grid item xs={8}>
                  <TextField
                    type="date"
                    variant="outlined"
                    fullWidth
                    value={dob}
                    onChange={handleDobChange}
                    sx={{ marginBottom: 0 }}
                  />
                </Grid>
              </Grid>
              <Grid
                container
                alignItems="center"
                spacing={2}
                sx={{ marginBottom: 2 }}
              >
                <Grid item xs={4}>
                  <FormLabel sx={{ marginBottom: 0 }}>Languages</FormLabel>
                </Grid>
                <Grid item xs={8}>
                  <FormControl component="fieldset">
                    <FormGroup row>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={languages.english}
                            onChange={handleLanguageChange}
                            name="english"
                          />
                        }
                        label="English"
                      />
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={languages.marathi}
                            onChange={handleLanguageChange}
                            name="marathi"
                          />
                        }
                        label="Marathi"
                      />
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={languages.hindi}
                            onChange={handleLanguageChange}
                            name="hindi"
                          />
                        }
                        label="Hindi"
                      />
                    </FormGroup>
                  </FormControl>
                </Grid>
              </Grid>
              <Grid
                container
                alignItems="center"
                spacing={2}
                sx={{ marginBottom: 2 }}
              >
                <Grid item xs={4}>
                  <InputLabel sx={{ marginBottom: 0 }}>Contact No</InputLabel>
                </Grid>
                <Grid item xs={8}>
                  <TextField
                    type="text"
                    variant="outlined"
                    placeholder="Contact No"
                    fullWidth
                    value={contactNo}
                    onChange={handleContactNoChange}
                    inputProps={{ maxLength: 10 }}
                    sx={{ marginBottom: 0 }}
                  />
                </Grid>
              </Grid>
            </>
          )}
          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <InputLabel sx={{ marginBottom: 0 }}>Email</InputLabel>
            </Grid>
            <Grid item xs={8}>
              <div style={{ position: "relative" }}>
                <TextField
                  type="email"
                  name="email"
                  {...register("email", {
                    required: true,
                    pattern: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
                  })}
                  variant="outlined"
                  placeholder="Email"
                  fullWidth
                  sx={{ marginBottom: 1 }}
                />
                {errors.email && errors.email.type === "required" && (
                  <p
                    className="errorMsg"
                    style={{
                      color: "red",
                      position: "absolute",
                      bottom: -30,
                      left: 0,
                    }}
                  >
                    Email is required.
                  </p>
                )}
                {errors.email && errors.email.type === "pattern" && (
                  <p
                    className="errorMsg"
                    style={{
                      color: "red",
                      position: "absolute",
                      bottom: -30,
                      left: 0,
                    }}
                  >
                    Email is not valid.
                  </p>
                )}
              </div>
            </Grid>
          </Grid>
          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <InputLabel sx={{ marginBottom: 0 }}>Password</InputLabel>
            </Grid>
            <Grid item xs={8}>
              <div style={{ position: "relative" }}>
                <TextField
                  type="password"
                  name="password"
                  {...register("password", {
                    required: true,
                    minLength: 6,
                  })}
                  variant="outlined"
                  placeholder="Password"
                  fullWidth
                  sx={{ marginBottom: 1 }}
                />
                {errors.password && errors.password.type === "required" && (
                  <p
                    className="errorMsg"
                    style={{
                      color: "red",
                      position: "absolute",
                      bottom: -30,
                      left: 0,
                    }}
                  >
                    Password is required.
                  </p>
                )}
                {errors.password && errors.password.type === "minLength" && (
                  <p
                    className="errorMsg"
                    style={{
                      color: "red",
                      position: "absolute",
                      bottom: -30,
                      left: 0,
                    }}
                  >
                    Password should be at-least 6 characters.
                  </p>
                )}
              </div>
            </Grid>
          </Grid>
          {isSignUp && (
            <Grid
              container
              alignItems="center"
              sx={{ marginBottom: 2 }}
              spacing={1}
            >
              <Grid item xs={4}>
                <InputLabel sx={{ marginBottom: 0 }}>
                  Confirm Password
                </InputLabel>
              </Grid>
              <Grid item xs={8}>
                <TextField
                  type="password"
                  variant="outlined"
                  placeholder="Confirm Password"
                  fullWidth
                />
              </Grid>
            </Grid>
          )}
          <Button
            type="Submit"
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            {isSignUp ? "Signup" : "Login"}
          </Button>
          <Button
            onClick={() => setisSignUp(!isSignUp)}
            sx={{ marginTop: 3, borderRadius: 3 }}
          >
            Change To {isSignUp ? "Login" : "Signup"}
          </Button>
        </Box>
      </form>
    </div>
  );
}
export default Authentication;
