import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { Box, Button, TextField, Typography, Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
  const [isLoggedIn, setIsLoggedIn] = useState(
    sessionStorage.getItem("login") === "true"
  );
  const navigate = useNavigate();

  const mystyle = {
    backgroundImage: "url('backgroundimg.jpg')",
    backgroundSize: "cover",
    minHeight: "100vh",
    width: "100vw",
    padding: "50px",
    backgroundRepeat: "no-repeat",
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({});

  const loginHandler = async (data) => {
    try {
      const result = await axios.post("http://localhost:5000/login", data, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      console.log("result from login", result);
      if (result.data) {
        sessionStorage.setItem("login", "true");
        sessionStorage.setItem("user", JSON.stringify(result.data.user));
        sessionStorage.setItem("accessToken", result.data.accessToken);
        sessionStorage.setItem("refreshToken", result.data.refreshToken);
        const sessionToken = result.data.accessToken;
        console.log("sessionToken:", sessionToken);

        // Immediately call refreshToken after login
        await refreshToken();

        await getData(sessionToken);
        alert("Logged in Successfully!!");
        setIsLoggedIn(true);
        navigate("/");
      } else {
        alert("Login failed, please check your credentials.");
      }
    } catch (error) {
      console.error("Login failed", error);
    }
  };

  const refreshToken = async () => {
    console.log("refreshToken function is called"); // Debugging line
    try {
      const response = await axios.post("http://localhost:5000/refresh", {
        refreshToken: sessionStorage.getItem("refreshToken"),
      });
      console.log("New access token:", response.data.accessToken); // Debugging line
      if (response.data.accessToken) {
        sessionStorage.setItem("accessToken", response.data.accessToken);
      }
    } catch (error) {
      console.error("Failed to fetch new access token", error);
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      // Define the refresh function
      const refresh = () => {
        console.log("Refreshing...");
        // Your refresh logic here
      };

      // Call refresh immediately
      refresh();

      // Set up the interval to call refresh every minute (60000 milliseconds)
      const intervalId = setInterval(refresh, 60000);

      // Clean up interval on component unmount
      return () => clearInterval(intervalId);
    }
  }, [isLoggedIn]);

  // useEffect(() => {
  //   console.log("useEffect is running"); // Debugging line

  //   if (sessionStorage.getItem("login") === "true") {
  //     const interval = setInterval(() => {
  //       console.log("Interval is firing"); // Debugging line
  //       refreshToken();
  //     }, 1 * 60 * 1000); // 1-minute interval

  //     return () => {
  //       console.log("Clearing interval"); // Debugging line
  //       clearInterval(interval); // Clean up the interval on component unmount
  //     };
  //   }
  // }, [isLoggedIn]);

  const getData = async (sessionToken) => {
    try {
      let result = await axios.get("http://localhost:5000/getData", {
        headers: {
          authorization: `Bearer ${sessionToken}`,
        },
      });
      console.log("result from getData", result.data);
    } catch (error) {
      console.error("Failed to fetch data", error);
    }
  };

  const handleLogout = () => {
    sessionStorage.removeItem("login");
    sessionStorage.removeItem("user");
    sessionStorage.removeItem("refreshToken");
    sessionStorage.removeItem("accessToken");
    setIsLoggedIn(false);
    alert("Logged out Successfully!!");
  };

  if (isLoggedIn) {
    return (
      <div style={mystyle}>
        <Box
          display={"flex"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            You are Already logged in!
          </Typography>
          <Button
            onClick={handleLogout}
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            Logout
          </Button>
        </Box>
      </div>
    );
  }

  return (
    <div style={mystyle}>
      <form onSubmit={handleSubmit(loginHandler)}>
        <Box
          display={"flex"}
          flexDirection={"column"}
          alignItems={"center"}
          justifyContent={"center"}
          margin={"auto"}
          marginTop={5}
          maxWidth={650}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <Typography variant="h2" padding={3} textAlign={"center"}>
            Login
          </Typography>

          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <Typography variant="body1">Email</Typography>
            </Grid>
            <Grid item xs={8}>
              <TextField
                type="email"
                style={{ background: "transparent" }}
                name="email"
                {...register("email", {
                  required: true,
                })}
                variant="outlined"
                placeholder="Email"
                fullWidth
              />
              {errors.email && errors.email.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Email is required.
                </p>
              )}
            </Grid>
          </Grid>

          <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ marginBottom: 1 }}
          >
            <Grid item xs={4}>
              <Typography variant="body1">Password</Typography>
            </Grid>
            <Grid item xs={8}>
              <TextField
                type="password"
                name="password"
                {...register("password", {
                  required: true,
                  minLength: 6,
                })}
                variant="outlined"
                placeholder="Password"
                fullWidth
              />
              {errors.password && errors.password.type === "required" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password is required.
                </p>
              )}
              {errors.password && errors.password.type === "minLength" && (
                <p className="errorMsg" style={{ color: "red" }}>
                  Password should be at least 6 characters.
                </p>
              )}
            </Grid>
          </Grid>

          <Button
            type="submit"
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            Login
          </Button>
          <Button
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
            onClick={() => navigate("/register")}
          >
            Register
          </Button>
        </Box>
      </form>
    </div>
  );
}

export default Login;

