// src/withSearch.js
import React, { useState } from "react";

const withSearch = (WrappedComponent, data) => {
  return function WithSearchComponent(props) {
    const [searchTerm, setSearchTerm] = useState("");
    const [isDropdownVisible, setIsDropdownVisible] = useState(false);

    const handleChange = (event) => {
      setSearchTerm(event.target.value);
      setIsDropdownVisible(true);
    };

    const handleItemClick = (item) => {
      setSearchTerm(item);
      setIsDropdownVisible(false);
    };

    const filteredData = data.filter((item) =>
      item.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
      <div style={{ position: "relative" }}>
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleChange}
          onFocus={() => setIsDropdownVisible(true)}
          onBlur={() => setTimeout(() => setIsDropdownVisible(false), 200)} // Delay to allow click
        />
        {isDropdownVisible && filteredData.length > 0 && (
          <ul
            style={{
              position: "absolute",
              border: "1px solid #ccc",
              width: "100%",
              zIndex: 1,
              background: "#fff",
              maxHeight: "150px",
              overflowY: "auto",
            }}
          >
            {filteredData.map((item, index) => (
              <li
                key={index}
                onClick={() => handleItemClick(item)}
                style={{ cursor: "pointer", padding: "5px" }}
              >
                {item}
              </li>
            ))}
          </ul>
        )}
        <WrappedComponent {...props} selectedItem={searchTerm} />
      </div>
    );
  };
};

export default withSearch;

