const initialState = {
  cartData: [],
};

function cartItem(state = initialState, action) {
  switch (action.type) {
    case "Add_To_Cart":
      return {
        ...state,
        cartData: [...state.cartData, action.payload],
      };
    case "Remove_From_Cart":
      return {
        ...state,
      };

    default:
      return state;
  }
}

export default cartItem;
