import React, { useState, useRef, useEffect } from "react";

const withSearch = (WrappedComponent, data) => {
  return function WithSearchComponent(props) {
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredData, setFilteredData] = useState([]);
    const [isDropdownVisible, setIsDropdownVisible] = useState(false);
    const [inputWidth, setInputWidth] = useState("auto");
    const [selectedItem, setSelectedItem] = useState(null);

    const inputRef = useRef(null);

    useEffect(() => {
      if (inputRef.current) {
        setInputWidth(inputRef.current.clientWidth);
      }
    }, []);

    const handleChange = (event) => {
      setSearchTerm(event.target.value);
      filterData(searchTerm);
      setIsDropdownVisible(true);
    };

    const handleItemClickInternal = (item) => {
      setSelectedItem(item); // Set selected item
      handleItemClick(item); // Call parent function with selected item
      setSearchTerm(""); // Clear search term
      setIsDropdownVisible(false); // Close dropdown
    };

    const filterData = (searchTerm) => {
      const filtered = data.filter((item) =>
        item.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredData(filtered);
    };
    return (
      <div style={{ position: "relative", display: "inline-block" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <input
            ref={inputRef}
            type="text"
            placeholder="Enter search term..."
            onFocus={() => setIsDropdownVisible(true)}
            onBlur={() => setTimeout(() => setIsDropdownVisible(false), 200)}
            onChange={handleChange}
            style={{
              cursor: "text",
              border: "1px solid #ccc",
              padding: "5px 10px",
              flex: "1",
              boxSizing: "border-box",
            }}
          />
          <span style={{ marginLeft: "5px" }}>SearchHere</span>
        </div>

        {isDropdownVisible && filteredData.length > 0 && (
          <ul
            style={{
              position: "absolute",
              border: "1px solid #ccc",
              width: inputWidth,
              zIndex: 1,
              background: "#fff",
              maxHeight: "150px",
              overflowY: "auto",
              listStyleType: "none",
              padding: 0,
              alignContent: "center",
              alignItems: "center",
              textAlign: "center",
            }}
          >
            {filteredData.map((item, index) => (
              <li
                key={index}
                onClick={() => handleItemClickInternal(item)}
                style={{ cursor: "pointer", padding: "5px" }}
              >
                {item}
              </li>
            ))}
          </ul>
        )}
        <WrappedComponent {...props} selectedItem={searchTerm} />
      </div>
    );
  };
};

export default withSearch;
